import React, { useState, useEffect } from "react";
import AliceCarousel from "react-alice-carousel";
import HomeSectionCard from "../HomeSectionCard/HomeSectionCard";
import { Button } from "@mui/material";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";
import { mens_kurta } from "../../../Data/men_kurta";

const HomeSectionCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [visibleItems, setVisibleItems] = useState(5); // default desktop

  const responsive = {
    0: { items: 1 },
    720: { items: 3 },
    1024: { items: 5.5 },
  };

  // detect screen width & update visible items count
  const updateVisibleItems = () => {
    if (window.innerWidth < 720) {
      setVisibleItems(1);
    } else if (window.innerWidth < 1024) {
      setVisibleItems(3);
    } else {
      setVisibleItems(5);
    }
  };

  useEffect(() => {
    updateVisibleItems();
    window.addEventListener("resize", updateVisibleItems);
    return () => window.removeEventListener("resize", updateVisibleItems);
  }, []);

  const slidePrev = () => setActiveIndex(activeIndex - 1);
  const slideNext = () => setActiveIndex(activeIndex + 1);

  const syncActiveIndex = ({ item }) => setActiveIndex(item);

  const items = mens_kurta.slice(0, 10).map((item, index) => (
    <HomeSectionCard product={item} key={index} />
  ));

  return (
    <div className="border">
      <div className="relative p-5">
        <AliceCarousel
          items={items}
          disableButtonsControls
          responsive={responsive}
          disableDotsControls
          onSlideChanged={syncActiveIndex}
          activeIndex={activeIndex}
        />

        {/* Next Button */}
        {activeIndex < items.length - visibleItems && (
          <Button
            onClick={slideNext}
            variant="contained"
            sx={{
              position: "absolute",
              top: "50%",
              right: 0,
              transform: "translateY(-50%) rotate(90deg)",
              backgroundColor: "white",
              color: "black",
              minWidth: "40px",
              width: "40px",
              height: "40px",
              borderRadius: "50%",
              boxShadow: 2,
              "&:hover": { backgroundColor: "#f0f0f0" },
            }}
            aria-label="next"
          >
            <KeyboardArrowLeftIcon sx={{ transform: "rotate(90deg)" }} />
          </Button>
        )}

        {/* Prev Button */}
        {activeIndex > 0 && (
          <Button
            onClick={slidePrev}
            variant="contained"
            sx={{
              position: "absolute",
              top: "50%",
              left: 0,
              transform: "translateY(-50%) rotate(-90deg)",
              backgroundColor: "white",
              color: "black",
              minWidth: "40px",
              width: "40px",
              height: "40px",
              borderRadius: "50%",
              boxShadow: 2,
              "&:hover": { backgroundColor: "#f0f0f0" },
            }}
            aria-label="previous"
          >
            <KeyboardArrowLeftIcon sx={{ transform: "rotate(90deg)" }} />
          </Button>
        )}
      </div>
    </div>
  );
};

export default HomeSectionCarousel;
